A = (0, 6);
B = (0, 0);
C = (5.196, 0);
M = 0.5 * B + 0.5 * C;
N = 0.5 * A + 0.5 * B;
P = 0.66 * N + 0.34 * C;
draw(A--B--C--cycle);
