draw(A--B--C--D--E--F--G--H--cycle);
draw(O--A);
draw(O--B);
draw(O--C);
draw(O--D);
draw(O--E);
draw(O--F);
draw(O--G);
draw(O--H);

label("$1$", (A + B)/2, S);
label("$2$", (B + C)/2, NE);
label("$4$", (C + D)/2, E);
