Z = (0,0);
Y = (12,0);
WW = (12,18);
X= (18,18);
draw(WW--Y);
draw(rightanglemark(WW,Y,Z,30));
draw(rightanglemark(Y,WW,X,30));
draw(WW--X--Y--Z--WW);
label("$W$",WW,N);
